"use strict";
let navBtnShow = document.querySelector(".s-header__nav-btn"),
    menu = document.querySelector(".s-header__nav"),
    navBtnClose = document.querySelector(".s-nav__close-btn"),
    bodyOverlay = document.querySelector(".s-header__overlay"),
    bodyLock = document.querySelector("body");

navBtnShow.addEventListener("click", function () {
    menu.classList.add("is-active"), bodyOverlay.classList.add("is-active"), bodyLock.classList.add("is-locked");
}),
    navBtnClose.addEventListener("click", function () {
        menu.classList.remove("is-active"), bodyOverlay.classList.remove("is-active"), bodyLock.classList.remove("is-locked");
    }),
    bodyOverlay.addEventListener("click", function () {
        menu.classList.remove("is-active"), bodyOverlay.classList.remove("is-active"), bodyLock.classList.remove("is-locked");
    });

